@layer base {
  :root {
    --background: 220 15% 6%;
    --foreground: 210 20% 98%;

    --card: 220 15% 9%;
    --card-foreground: 210 20% 98%;

    --popover: 220 15% 9%;
    --popover-foreground: 210 20% 98%;

    --primary: 345 80% 58%;
    --primary-foreground: 210 20% 98%;
    --primary-glow: 345 100% 68%;

    --secondary: 260 60% 55%;
    --secondary-foreground: 210 20% 98%;

    --muted: 220 15% 15%;
    --muted-foreground: 210 20% 60%;

    --accent: 260 60% 55%;
    --accent-foreground: 210 20% 98%;

    --destructive: 0 62% 50%;
    --destructive-foreground: 210 20% 98%;

    --border: 220 15% 15%;
    --input: 220 15% 15%;
    --ring: 345 80% 58%;

    --radius: 0.75rem;

    --gradient-cinema: linear-gradient(135deg, hsl(345 80% 58% / 0.15), hsl(260 60% 55% / 0.15));
    --gradient-poster: linear-gradient(180deg, transparent 0%, hsl(220 15% 6% / 0.9) 100%);
    --gradient-hero: linear-gradient(135deg, hsl(345 80% 58%), hsl(260 60% 55%));

    --shadow-cinema: 0 20px 60px -15px hsl(345 80% 58% / 0.4);
    --shadow-poster: 0 8px 32px -8px hsl(220 15% 0% / 0.6);

    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

