export interface Movie {
  id: number;
  title: string;
  year: number;
  genre: string[];
  rating: number;
  poster: string;
  description: string;
  director: string;
}

export interface UserRating {
  movieId: number;
  rating: number;
}

export interface UserPreferences {
  genres: string[];
  ratings: UserRating[];
  viewHistory: number[];
}

import { Movie } from "@/types/movie";
import { Star, Eye } from "lucide-react";
import { cn } from "@/lib/utils";

interface MovieCardProps {
  movie: Movie;
  userRating?: number;
  onRate: (movieId: number, rating: number) => void;
  onView: (movieId: number) => void;
  isViewed?: boolean;
}

export function MovieCard({ movie, userRating, onRate, onView, isViewed }: MovieCardProps) {
  const handleStarClick = (rating: number) => {
    onRate(movie.id, rating);
  };

  return (
    <div
      className="group relative bg-card rounded-lg overflow-hidden shadow-poster hover:shadow-cinema transition-all duration-300 hover:scale-105 cursor-pointer animate-slide-in"
      onClick={() => onView(movie.id)}
    >
      <div className="relative aspect-[2/3] overflow-hidden">
        <img
          src={movie.poster}
          alt={movie.title}
          className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
        />
        <div className="absolute inset-0 bg-gradient-poster opacity-0 group-hover:opacity-100 transition-opacity duration-300" />

        {isViewed && (
          <div className="absolute top-3 right-3 bg-primary rounded-full p-2">
            <Eye className="w-4 h-4 text-primary-foreground" />
          </div>
        )}

        <div className="absolute bottom-0 left-0 right-0 p-4 translate-y-full group-hover:translate-y-0 transition-transform duration-300">
          <p className="text-sm text-muted-foreground line-clamp-3 mb-2">
            {movie.description}
          </p>
          <p className="text-xs text-muted-foreground">
            Directed by {movie.director}
          </p>
        </div>
      </div>

      <div className="p-4">
        <h3 className="font-semibold text-lg mb-1 line-clamp-1">{movie.title}</h3>
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm text-muted-foreground">{movie.year}</span>
          <div className="flex items-center gap-1">
            <Star className="w-4 h-4 fill-primary text-primary" />
            <span className="text-sm font-medium">{movie.rating}</span>
          </div>
        </div>

        <div className="flex flex-wrap gap-1 mb-3">
          {movie.genre.slice(0, 3).map((genre) => (
            <span
              key={genre}
              className="text-xs px-2 py-1 bg-muted rounded-md text-muted-foreground"
            >
              {genre}
            </span>
          ))}
        </div>

        <div className="flex items-center gap-1" onClick={(e) => e.stopPropagation()}>
          {[1, 2, 3, 4, 5].map((star) => (
            <button
              key={star}
              onClick={() => handleStarClick(star)}
              className="transition-transform hover:scale-125"
            >
              <Star
                className={cn(
                  "w-5 h-5 transition-colors",
                  userRating && star <= userRating
                    ? "fill-primary text-primary"
                    : "fill-muted text-muted"
                )}
              />
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

import { GENRES } from "@/data/movies";
import { cn } from "@/lib/utils";
import { Sparkles } from "lucide-react";

interface PreferenceSelectorProps {
  selectedGenres: string[];
  onToggleGenre: (genre: string) => void;
}

export function PreferenceSelector({ selectedGenres, onToggleGenre }: PreferenceSelectorProps) {
  return (
    <div className="bg-card rounded-lg p-6 shadow-poster">
      <div className="flex items-center gap-2 mb-4">
        <Sparkles className="w-5 h-5 text-primary" />
        <h2 className="text-xl font-semibold">Your Preferences</h2>
      </div>
      <p className="text-sm text-muted-foreground mb-4">
        Select your favorite genres to get personalized recommendations
      </p>
      <div className="flex flex-wrap gap-2">
        {GENRES.map((genre) => {
          const isSelected = selectedGenres.includes(genre);
          return (
            <button
              key={genre}
              onClick={() => onToggleGenre(genre)}
              className={cn(
                "px-4 py-2 rounded-lg font-medium transition-all duration-200",
                isSelected
                  ? "bg-gradient-hero text-primary-foreground shadow-cinema"
                  : "bg-muted text-muted-foreground hover:bg-muted/80"
              )}
            >
              {genre}
            </button>
          );
        })}
      </div>
    </div>
  );
}

import { useState, useEffect, useMemo } from "react";
import { Movie } from "@/types/movie";
import { MOVIES } from "@/data/movies";

export interface UserRating {
  movieId: number;
  rating: number;
}

export function useRecommendations() {
  const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
  const [userRatings, setUserRatings] = useState<UserRating[]>([]);
  const [viewHistory, setViewHistory] = useState<number[]>([]);

  // Load from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem("moviePreferences");
    if (stored) {
      const data = JSON.parse(stored);
      setSelectedGenres(data.selectedGenres || []);
      setUserRatings(data.userRatings || []);
      setViewHistory(data.viewHistory || []);
    }
  }, []);

  // Save to localStorage whenever preferences change
  useEffect(() => {
    localStorage.setItem(
      "moviePreferences",
      JSON.stringify({ selectedGenres, userRatings, viewHistory })
    );
  }, [selectedGenres, userRatings, viewHistory]);

  const toggleGenre = (genre: string) => {
    setSelectedGenres((prev) =>
      prev.includes(genre) ? prev.filter((g) => g !== genre) : [...prev, genre]
    );
  };

  const rateMovie = (movieId: number, rating: number) => {
    setUserRatings((prev) => {
      const existing = prev.find((r) => r.movieId === movieId);
      if (existing) {
        return prev.map((r) => (r.movieId === movieId ? { movieId, rating } : r));
      }
      return [...prev, { movieId, rating }];
    });
  };

  const addToHistory = (movieId: number) => {
    setViewHistory((prev) => {
      if (prev.includes(movieId)) return prev;
      return [...prev, movieId];
    });
  };

  const recommendations = useMemo(() => {
    return getRecommendations(MOVIES, selectedGenres, userRatings, viewHistory);
  }, [selectedGenres, userRatings, viewHistory]);

  return {
    selectedGenres,
    userRatings,
    viewHistory,
    toggleGenre,
    rateMovie,
    addToHistory,
    recommendations,
    allMovies: MOVIES,
  };
}

function getRecommendations(
  movies: Movie[],
  selectedGenres: string[],
  userRatings: UserRating[],
  viewHistory: number[]
): Movie[] {
  if (selectedGenres.length === 0 && userRatings.length === 0) {
    return movies.slice(0, 6);
  }

  const scoredMovies = movies.map((movie) => {
    let score = 0;

    // Genre matching (highest weight)
    const genreMatch = movie.genre.filter((g) => selectedGenres.includes(g)).length;
    score += genreMatch * 3;

    // User ratings influence
    const highRatedGenres = userRatings
      .filter((r) => r.rating >= 4)
      .map((r) => movies.find((m) => m.id === r.movieId))
      .filter(Boolean)
      .flatMap((m) => m!.genre);

    const ratedGenreMatch = movie.genre.filter((g) => highRatedGenres.includes(g)).length;
    score += ratedGenreMatch * 2;

    // Boost highly rated movies
    if (movie.rating >= 8.5) score += 1;

    // Penalize already viewed
    if (viewHistory.includes(movie.id)) score -= 10;

    return { movie, score };
  });

  return scoredMovies
    .sort((a, b) => b.score - a.score)
    .slice(0, 6)
    .map((item) => item.movie);
}

